mapper.select:
SELECT essay.essayId as essayid,essay.essayname,essay.essaysrc,essay.description,essay.finishtime,essay.onlin,admin.adminId,admin.`name`
FROM essay,admin
WHERE  essay.adminId=admin.adminId AND essay.essayId=#{status}

SELECT essay.essayId as essayid,essay.essayname,essay.essaysrc,essay.description,essay.finishtime,essay.onlin,admin.adminId,admin.`name`
FROM essay,admin
WHERE  essay.adminId=admin.adminId AND essay.name=#{status}
